document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector("input"),t=document.querySelector("button"),o=document.querySelector(".post-block"),n=document.querySelector(".comment-button"),c=document.querySelector(".comment-block");t.addEventListener("click",(()=>{const t=e.value;t>=1&&t<=100?fetch(`https://jsonplaceholder.typicode.com/posts/${t}`).then((e=>e.json())).then((e=>{o.innerHTML=`\n            <h2>${e.title}</h2>\n            <p>${e.body}</p>\n          `,n.style.display="block",n.addEventListener("click",(()=>{fetch(`https://jsonplaceholder.typicode.com/posts/${t}/comments`).then((e=>e.json())).then((e=>{let t="";e.forEach((e=>{t+=`\n                    <h3>${e.name}</h3>\n                    <p>${e.body}</p>\n                  `})),c.innerHTML=t})).catch((e=>console.error(e)))}))})).catch((e=>console.error(e))):alert("ID має бути від 1 до 100")}))}));